import React from "react";
import styled from "styled-components";
import img from './bumsu.jpeg';
import Menubar from "./Menubar";
import { useState } from 'react';
import { lightTheme, darkTheme } from '../../theme';
import { GlobalStyles } from "../../global";
import Toggle from "../../Toggle";
import { ThemeProvider } from "styled-components";
const Profilebox= styled.div`
        position:relative;
        width:800px;
        height:700px;
        top:500px;
        left:50%;
        transform:translateX(-50%);
        border:1px solid black;
        border-radius:4em;
        li{
            margin:15px 0px;
        }
`;

function Profile() { 

    const [theme, setTheme] = useState('light');
    const toggleTheme = () => {
        // if the theme is not light, then set it to dark
        if (theme === 'light') {
            setTheme('dark');
            // otherwise, it should be light
        } else {
            setTheme('light');
        }
    }   
        return (
        <div>
            <Profilebox>
                <h2 style={{textAlign:"center"}}>Web Developer Portfolio</h2>
                    <img style={{ width:"300px",border:"1px solid black",borderRadius:"8em",marginLeft:"40px"}} src={img}></img>
                     <ul style={{listStyle:"none",float:"right"}}>
                     <div>
                    <Toggle theme={theme} toggleTheme={toggleTheme} />
                   {theme === 'light' ? 'Light Mode' : 'Dark Mode'}
                  </div>
                         <hr></hr>
                         <br></br>
                        <li>
                            <strong>Programming Language</strong>: Java, HTML, CSS, Python
                        </li>
                        <li>
                            <strong>Frameworks</strong>: Spring, React.js, Django, JSP
                        </li>
                        <li>
                            <strong>Name</strong>: Bumsu Kim  
                        </li>
                        <li>
                            <strong>Email</strong>: qjatn50089@gmail.com    
                        </li>
                        <li>
                            <strong>Phone</strong>: 010-2784-0955
                        </li>
                        <li>
                            <strong>School</strong>: Hallym University
                        </li>
                        <h2>About Me</h2>
                        Hello I'm handsome Web Developer
                     </ul>

                </Profilebox>
                <Menubar></Menubar>
                <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>

                <section style={{position:"relative",top:"1000px"}}></section>
                  <GlobalStyles />
          </ThemeProvider>
               </div> 
  ); 
};

export default Profile;
