import React, { Component, useEffect} from 'react';
import { Route, Switch } from 'react-router-dom';
import Profile from './hoc/Menu/Profile';
import Profile_PJ from './hoc/Menu/Profile_PJ';
import styled, { ThemeProvider } from 'styled-components';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import Accordion from './Accordion';

const AC = styled.div`
.wrapper{
    display:flex;
    height:100vh;
    width:100vw;
    justify-content:center;
    align-items:center;
}
.accordion{
    width:500px;
}
.item{
    background:yellow;
    margin-bottom:5px;
    padding:10px 20px;

}
.title{
    color:green;
    display:flex;
    justify-content:space-between;
    align-items:center;
    ccursor:pointer;
}
.content{
    color:blue;
    max-height:0;
    overflow:hidden;
    transition: all 0.5s cubic-bezier(0,1,0,1);
}
.content.show{
    height:auto;
    max-height:9999p;
    transition:all 0.5s cubic-bezier(1,0,1,0);
}
`;
function App() {
    // App.js
    const toggle =(i) =>{
        if(selected == i ){
            return setSeleted(null);
        }
        setSeleted(i);
    }
    const [selected,setSeleted]=useState(null);
    const [message, setMessage] = useState("");

    useEffect(() => {
        fetch('/home1')
        .then(response => response.text())
        .then(message => {
        setMessage(message);
        });
        },[])

    return (
        <AC>
        <div className='wrapper'>
            <Switch>
                    <Route exact path="/" component={Profile} />
                    <Route path="/Profile_PJ" component={Profile_PJ}>
                    <h1>React Accordion</h1>
      <div className="accordion">

            {data.map((item,i)=> (
                <div className='item'>
                    <div className='title' onClick={()=> toggle(i)}>
                        <h2>{item.question}</h2>
                        <span>{selected == i ? '-' : '+'} </span>
                    </div>
                    <div className={selected == i ? 'cotent show' : 'content'}>{item.answer}</div>
                </div>
            ))}
      </div>

                    </Route>
            </Switch>

            {/*<Switch2 value={value} onChange={value => setValue(value)} /> */}
            {/*   <ThemeProvider theme={theme}>
                <S.Main>

                    <Button title={theme === 'light' ? '일반모드로 테마 변경하기' : '다크모드로 테마 변경하기'}
                        click={toggleTheme} />

                </S.Main>


    </ThemeProvider>
            */}

        </div>
        </AC>
    );

}
const data=[
    {
        question:'hello',
        answer:
        'ggafkgoakogako ' 
    }
]


export default App;
const S = {};
S.Main = styled.div`
  width: 100%;
  height: 100vh;
  background-color: ${props => props.theme.colors.bgColor};
`;
