import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import { Home, About } from './pages';
import Menubar from './hoc/Menu/Menubar';
import Profile from './hoc/Menu/Profile';
import Profile_PJ from './hoc/Menu/Profile_PJ';
import { Link } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import styled, { ThemeProvider } from 'styled-components';
import Button from './components/Button';

import useTheme from './useTheme';
import { useState } from 'react';
import Switch1 from './Switch';
import Switch2 from 'expo-dark-mode-switch';
import { lightTheme, darkTheme } from './theme';
import { GlobalStyles } from './global';

function App() {
    // App.js
const [theme, setTheme] = useState('light');
const toggleTheme = () => {
    // if the theme is not light, then set it to dark
    if (theme === 'light') {
      setTheme('dark');
    // otherwise, it should be light
    } else {
      setTheme('light');
    }
  }
     return (
        <div>
          <ThemeProvider theme={lightTheme}>
      <>
        <GlobalStyles />
        <button>Toggle theme</button>
        <h1>It's a light theme!</h1>
        <footer>
        </footer>
      </>
    </ThemeProvider>
         {/*<Switch2 value={value} onChange={value => setValue(value)} /> */}
        {/*   <ThemeProvider theme={theme}>
                <S.Main>

                    <Button title={theme === 'light' ? '일반모드로 테마 변경하기' : '다크모드로 테마 변경하기'}
                        click={toggleTheme} />

                </S.Main>


    </ThemeProvider>
            */}
    <Switch>
                <Route exact path="/" component={Profile} />
                <Route path="/Profile_PJ" component={Profile_PJ} />
            </Switch>

        </div>
    );

}

export default App;
const S = {};
S.Main = styled.div`
  width: 100%;
  height: 100vh;
  background-color: ${props => props.theme.colors.bgColor};
`;
