import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import { Home, About } from './pages';
import Menubar from './hoc/Menu/Menubar';
import Profile from './hoc/Menu/Profile';
import Profile_PJ from './hoc/Menu/Profile_PJ';
import { Link } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import styled, { ThemeProvider } from 'styled-components';
import Button from './components/Button';
import { dark } from './theme';
import { light } from './theme';
import useTheme from './useTheme';
import { useState } from 'react';

function App() {

    const [themeMode, setThemeMode] = useState('light');
    const theme = themeMode === 'light' ? light : dark;
    const toggleTheme = () => setThemeMode(themeMode === 'light' ? 'dark' : 'light'); // 테마 변경하기 이벤트

    return (
        <div>
            <ThemeProvider theme={theme}>
                <S.Main>
                    <Button title={theme === 'light' ? '일반모드로 테마 변경하기' : '다크모드로 테마 변경하기'}
                        click={toggleTheme} />
                </S.Main>
            </ThemeProvider>

        </div>
    );

}

export default App;
const S = {};
S.Main = styled.div`
  width: 100%;
  height: 100vh;
  background-color: ${props => props.theme.colors.bgColor};
`;
